var max2 = msg.payload[1].max;


// delta calculation on the last 24 hours
var delta = max2 - max1;
var deltapercent = 100 * delta/max1;
deltapercent = deltapercent.toFixed(2);

// 
delta = delta.toFixed(2)
if (delta > 0){
    delta = "+" + delta + " Kg";
}
else{
    delta = delta + " Kg";
}



// return
msg.thedelta = delta;
msg.thedeltapercent = deltapercent;

return msg;

