array = msg.payload;

let data = new Array(arraysize-1);

for (let i=0 ; i<arraysize ; i++){
    data[i]= array[i].max;
}


// delta calculation on the last 7d
delta7d = data[arraysize-1] - data[0];
var delta7dpercent = 100 * delta7d/data[0];
delta7dpercent = delta7dpercent.toFixed(2);

// 
delta7d = delta7d.toFixed(2)
if (delta7d > 0){
    delta7d = "+" + delta7d + " Kg";
}
else{
    delta7d = delta7d + " Kg";
}

// return
msg.thedelta7d = delta7d;
msg.thedelta7dpercent = delta7dpercent;
msg.thedata = data;

return msg;

