/*
* Title:    Beehive - Payload conversion
* Autor:    Antoine AUGAGNEUR
* Date:     april, 2022
* Sources:  --
*
*/

// Get Payload
var buff = msg.payload.received_at; // get the date
var dateformat = Date.parse(buff);

var DeviceTime_date = new Date(1970, 0, 1);                        // Epoch
DeviceTime_date.setSeconds((dateformat/1000)+(3600*2));
let DeviceTime_date_format = DeviceTime_date.toLocaleString('fr-FR',{
                                                weekday: 'long',
                                                year: 'numeric',
                                                month: 'long',
                                                day: 'numeric',
                                                hour: 'numeric',
                                                minute: 'numeric',
                                                second: 'numeric'});





// New msg creation
var newMsg = { payload: DeviceTime_date_format };
return newMsg;

