*
*/

//!\\ topic and paylaod only correct for ThingPark Community NS //!\\

// Get values of the form
// (paths example below are specific to ThingPark Community NS)
var command = msg.payload.thepayload;                           //!\\ set your own path
var deveui = msg.payload.deveui;                                //!\\ set your own path
var port = msg.payload.port;                                    //!\\ set your own path

// Creation of the topic
// (topic example below is specific to ThingPark Community NS)
var topic = "mqtt/things/" + deveui + "/downlink";              //!\\ set your own topic

// Creation of the variable to return
var P=
{
    "topic": topic,             
}

P.payload =                                                     //!\\ set your own payload format
{
	"DevEUI_downlink": {
		"DevEUI": deveui,
		"FPort": port,
		"payload_hex": command,
		"Confirmed": "0",
		"FlushDownlinkQueue": "1"    
	}

}


msg=P;
return msg;

/*
* Title:    Clock Synchronization - Uplink analyzer
* Autor:    Antoine AUGAGNEUR
* Date:     may, 2022
* Sources:  LoRaWAN Fragmented Data Block Transport v1.0.0 Specification
*
*/

// Uplink analysis
DevEUI = msg.payload.DevEUI_uplink.DevEUI;              //!\\ set your own path
upport = msg.payload.DevEUI_uplink.FPort;               //!\\ set your own path
Fcnt = msg.payload.DevEUI_uplink.FCntUp;                //!\\ set your own path
message = msg.payload.DevEUI_uplink.payload_hex;        //!\\ set your own path



// Clock command analysis
if (upport == "201"){

    message = message.toString('hex');

    if (message[1] == "0"){
        msg.commandtype = "PackageVersionAns ";
        msg.analysis = "Pack.ID: " + message[2] + message[3] + " / " + "Pack.Ver: " +message[4] + message[5];
    }
    else if(message[1] == "1"){
        msg.commandtype = "FragSessionStatusAns";

        // Received&index = 2-bits Fragindex + 14-bits NbFragReceived
        var Received_Index = message[2] + message[3];
        var Received_Index_bin = hex2bin(Received_Index);
        var FragIndex_bin = Received_Index_bin[0]
                            + Received_Index_bin[1]
        var FragIndex_dec= parseInt(FragIndex_bin, 2);

        var NbFragReceived_bin = Received_Index_bin[2]
                                + Received_Index_bin[3]
                                + Received_Index_bin[4]
                                + Received_Index_bin[5]
                                + Received_Index_bin[6]
                                + Received_Index_bin[7]
                                + Received_Index_bin[8]
                                + Received_Index_bin[9]
                                + Received_Index_bin[10]
                                + Received_Index_bin[11]
                                + Received_Index_bin[12]
                                + Received_Index_bin[13]
                                + Received_Index_bin[14]
                                + Received_Index_bin[15]
        var NbFragReceived_dec= parseInt(NbFragReceived_bin, 2);

        // MissingFrag
        var MissingFrag = message[4];
        var MissingFrag_dec = parseInt(MissingFrag, 16);

        // Status
        var Status = message[5];
        var Status_msg = "OK";
        if (Status == '1'){
            Status_msg = "Not enough matrix memory"; // number of missing fragments > available memory matrix storage capacity
        }

        msg.analysis = "Session: " + FragIndex_dec + " / Frag received: " + NbFragReceived_dec + " / Status: " + Status_msg;
    }
    else if(message[1] == "1"){
        msg.commandtype = "AppTimeReq";
        var thedate1 = SecToDate_1(message);
        msg.analysis = thedate1;
    }
    else{
        msg.commandtype = "...";
        msg.analysis = "...";
    }

}
else{
    msg.commandtype = "...";
    msg.analysis = "...";
}



return msg;







function hex2bin(hex){
    return ("00000000" + (parseInt(hex, 16)).toString(2)).substr(-8);
}
