/*
* Title:    Fragmentation - Uplink analyzer
* Autor:    Antoine AUGAGNEUR
* Date:     may, 2022
* Sources:  LoRaWAN Fragmented Data Block Transport v1.0.0 Specification
*
*/

// Uplink analysis
//DevEUI = msg.payload.DevEUI_uplink.DevEUI;              //!\\ set your own path
var upport = msg.payload.DevEUI_uplink.FPort;               //!\\ set your own path
//Fcnt = msg.payload.DevEUI_uplink.FCntUp;                //!\\ set your own path
var message = msg.payload.DevEUI_uplink.payload_hex;        //!\\ set your own path



// Clock command analysis
if (upport == "201"){

    message = message.toString('hex');

// ----------------------------------------------------------------------------------------------------------------------------
    if (message[1] == "0"){
        msg.commandtype = "PackageVersionAns ";
        msg.analysis = "Pack.ID: " + message[2] + message[3] + " / " + "Pack.Ver: " +message[4] + message[5];
    }
// ----------------------------------------------------------------------------------------------------------------------------
    else if(message[1] == "1"){
        msg.commandtype = "FragSessionStatusAns";

        // Received&index = 2-bits Fragindex + 14-bits NbFragReceived
        var Received_Index = message[4] + message[5]+ message[2]+ message[3];           // !!! big endian - little endian
        var Received_Index_bin = hex2bin(Received_Index,2);
        var FragIndex_bin = Received_Index_bin[0]
                            + Received_Index_bin[1]
        var FragIndex_dec= parseInt(FragIndex_bin, 2);

        var NbFragReceived_bin = Received_Index_bin[2]
                                + Received_Index_bin[3]
                                + Received_Index_bin[4]
                                + Received_Index_bin[5]
                                + Received_Index_bin[6]
                                + Received_Index_bin[7]
                                + Received_Index_bin[8]
                                + Received_Index_bin[9]
                                + Received_Index_bin[10]
                                + Received_Index_bin[11]
                                + Received_Index_bin[12]
                                + Received_Index_bin[13]
                                + Received_Index_bin[14]
                                + Received_Index_bin[15]
        var NbFragReceived_dec= parseInt(NbFragReceived_bin, 2);

        // MissingFrag
        var MissingFrag = message[6] + message[7];
        var MissingFrag_dec = parseInt(MissingFrag, 16);

        // Status
        var Status = message[9]; // message[8] is RFU (always 0)
        var Status_msg = "OK";
        if (Status == '1'){
            Status_msg = "Not enough matrix memory"; // number of missing fragments > available memory matrix storage capacity
        }

        msg.analysis = "Session: " + FragIndex_dec + " / Frag received: " + NbFragReceived_dec + " / Missing Frag: " + MissingFrag_dec +  " / Status: " + Status_msg;
    }
// ----------------------------------------------------------------------------------------------------------------------------
    else if(message[1] == "2"){
        msg.commandtype = "FragSessionSetupAns";

        // StatusBitMask (1)
        var StatusBitMask = message[2] + message[3];
        var StatusBitMask_bin = hex2bin(StatusBitMask,1);

        // FragIndex [7:6]
        var fragIndex = StatusBitMask_bin[0] + StatusBitMask_bin[1];
        var fragIndex_dec = parseInt(fragIndex,2);

        // RFU [5:4]
        // it's 0

        // Wrong Descriptor [3]
        var WrongDescriptor = "OK";
        if (StatusBitMask_bin [4] == 1){
            WrongDescriptor = "Wrong"
        }

        // FragSession index not supported [2]
        var FragSessionIndex = "OK";
        if (StatusBitMask_bin [5] == 1){
            FragSessionIndex = "Not supported";
        }

        // Not enough Memory [1]
        var NEMemory = "OK";
        if (StatusBitMask_bin [6] == 1){
            NEMemory = "Not enough";
        }

        // Encoding unsupported [0]
        var EncodeUnsupp = "OK";
        if (StatusBitMask_bin [7] == 1){
            EncodeUnsupp = "Not supported";
        }

        var error = "";
        if ( StatusBitMask_bin [4] == 1 || StatusBitMask_bin [5] == 1 || StatusBitMask_bin [6] == 1 || StatusBitMask_bin [7] == 1) {
             error = "Command was not accepted ! -> "
        }

        msg.analysis = error + "FragIndex: " + fragIndex_dec + " / Descriptor: " + WrongDescriptor + " / FragSessionIndex: " + FragSessionIndex + " / Memory: " + NEMemory + " / Encoding: " + EncodeUnsupp;

    }
// ----------------------------------------------------------------------------------------------------------------------------
    else if(message[1] == "3"){
        msg.commandtype = "FragSessionDeleteAns";

        // Status (1)
        var status = message[2] + message[3];
        var status_bin = hex2bin(status,1);

        // RFU [7:3]
        // it's 0

        // FragIndex [1:0]
        var fragindex = status_bin[6] + status_bin[7];
        var fragindex_dec = parseInt(fragindex,2);

        var deletemsg; 

        // Session [2]
        if (status_bin [5] == 1){
            deletemsg = "Command not accepted ! -> Session n° " + fragindex_dec + " does not exist.";
        }
        else {
            deletemsg = " Session n° " + fragindex_dec + " was deleted.";
        }

        msg.analysis = deletemsg;
        }
// ----------------------------------------------------------------------------------------------------------------------------
    else{
        msg.commandtype = "...";
        msg.analysis = "...";
    }

}
else{
    msg.commandtype = "...";
    msg.analysis = "...";
}



return msg;







function hex2bin(hex,nboctet){
    var bin;
    if (nboctet == 1){
        bin = ("00000000" + (parseInt(hex, 16)).toString(2)).substr(-8);
    }
    else if (nboctet == 2){
        bin = ("0000000000000000" + (parseInt(hex, 16)).toString(2)).substr(-16);
    }
    return bin;
}

/*
* Title:    Fragmetnation -  FragSessionSetupReq creation
* Autor:    Antoine AUGAGNEUR
* Date:     may, 2022
* Sources:  LoRaWAN Fragmented Data Block Transport v1.0.0 Specification
*
*/


// get the form values
var FragIndex = msg.payload.fragindex;
var McGroupBitMask = msg.payload.mcgroupbitmask ;
var FragNumber = msg.payload.nbfrag;
var Fragsize = msg.payload.fragsize;
var FragAlgo = msg.payload.fragalgo;
var AckDelay = msg.payload.blockackdelay;
var padding = msg.payload.padding;
var Descriptor = msg.payload.descriptor;


// FragSession (1)
FragSession_bin = "00" + ("00" + (parseInt(FragIndex, 10)).toString(2)).substr(-2) + McGroupBitMask;        // FragIndex in 0b + McGroupBitMask
FragSession = ("00" + (parseInt(FragSession_bin,2)).toString(16)).substr(-2);                               // bin to hex conversion


// NbFrag (2)
var nbFrag = ("0000" + (parseInt(FragNumber,10)).toString(16)).substr(-4);                                  // Number of fragment with 2 bytes
var NbFrag = nbFrag[2] + nbFrag[3] + nbFrag[0] + nbFrag[1];                                                 // little endian - big endian

// FragSize (1)
var FragSize = ("00" + (parseInt(Fragsize,10)).toString(16)).substr(-2);                                    // Fragment size with 1 byte

// Control (1)
var Control_bin = "00"
                    + ("000" + (parseInt(FragAlgo, 10)).toString(2)).substr(-3)
                    + ("000" + (parseInt(AckDelay, 10)).toString(2)).substr(-3);
var Control = ("00" + (parseInt(Control_bin,2)).toString(16)).substr(-2); 

// Padding (1) 
var Padding = ("00" + (parseInt(padding,10)).toString(16)).substr(-2);

// Descriptor (4)
// already setup in the form node

// msg for command
var FragSessionSetupReq = "02"; // 02 is CID
FragSessionSetupReq += FragSession + NbFrag + FragSize + Control + Padding + Descriptor;

msg.payload = FragSessionSetupReq;
return msg;

/*
* Title:    Fragmentation -  Downlink Data Fragment creation
* Autor:    Antoine AUGAGNEUR
* Date:     may, 2022
* Sources:  LoRaWAN Fragmented Data Block Transport v1.0.0 Specification
*
*/


// get the form values
var FragIndex = msg.payload.fragindex;
var N_codedfrag = msg.payload.N ;
var CodedFrag = msg.payload.CodedF;


// IndexN (2)
var IndexN_bin = ("00" + (parseInt(FragIndex, 10)).toString(2)).substr(-2)
                + ("00000000000000" + (parseInt(N_codedfrag, 10)).toString(2)).substr(-14);                   // FragIndex in 0b + McGroupBitMask
var IndexN = (parseInt(IndexN_bin,2)).toString(16);                                                         // bin to hex conversion

// CODED Fragment (0:MaxAppPl-3)
// already setup in the form node

// msg for command
var DownlinkDataFrag = "08"; // 08 is CID
DownlinkDataFrag += IndexN + CodedFrag;

msg.payload = DownlinkDataFrag;
return msg;

/*
* Title:    Fragmentation -  Object array to classic 2D array
