var coded_f_array_size_col = Object.keys(coded_f_array[0]).length;

// Object to standard 2D array
for (let i=0 ; i<coded_f_array_size_line ; i++){
    coded_f_array[i] = Object.values(coded_f_array[i]);
}

// global variables
flow.set("coded_f_array_size_line",coded_f_array_size_line);
flow.set("coded_f_array_size_col",coded_f_array_size_col);
