/*
* Title:    Fragmentation -  Downlink Data Fragment creation
* Autor:    Antoine AUGAGNEUR
* Date:     may, 2022
* Sources:  LoRaWAN Fragmented Data Block Transport v1.0.0 Specification
*
*/

// Save the DEVEUI
flow.set("DEVEUI",msg.payload.deveui);    

// get the global variable
var nbline = flow.get("coded_f_array_size_line");   // number of coded fragments
var nbcol = flow.get("coded_f_array_size_col");     // size of fragment (in bytes)
var arraydata = flow.get("coded_f_array");          // the array of coded fragments

// DataFragment commands setup
var coded_f_array_final = Array(nbline);            // array setup
var IndexN_bin_buffer;                              // used in the for loop
var IndexN_buffer;                                  // used in the for loop
var FragIndex_bin = msg.payload.fragindex;          // get the payload
FragIndex_bin = ("00" + (parseInt(FragIndex_bin, 10)).toString(2)).substr(-2)

for (let p=0 ; p<nbline ; p++){

    IndexN_bin_buffer = FragIndex_bin + ("00000000000000" + (parseInt((p+1), 10)).toString(2)).substr(-14);     // p is the Nth data fragment
    IndexN_buffer = ("0000" + (parseInt(IndexN_bin_buffer,2)).toString(16)).substr(-4);                                           // bin to hex conversion

    // DataFragment command n°p
    coded_f_array_final[p] = "08" + IndexN_buffer[2] + IndexN_buffer[3] + IndexN_buffer[0] + IndexN_buffer[1];             // little - big endian

    for (let j=0 ; j<nbcol ; j++){
        coded_f_array_final[p] += arraydata[p][j];
    }
}


// global variables
flow.set("coded_f_array_final",coded_f_array_final);    // data to send line by line
flow.set("counter",0);                             // counter to send data line by line




msg.Status = "Ready for lauch ! ";
msg.debug = coded_f_array_final;
return msg;

